import React from 'react';
import { useSelector } from 'react-redux';
import { insertBook } from '../store/bookSlice';

const BooksList = ({ isLoading, books }) => {

  const { isLoggedIn } = useSelector((state) => state.auth)

  const bookList = books.length > 0 ?books.map(itme => (
    <li className='list-group-item d-flex  justify-content-between align-items-center' key={itme.id}>
      <div>{itme.title}</div>
      <div className='btn-group' role='group'>
        <button type='button' className='btn btn-primary' disabled={!isLoggedIn}>
          Read
        </button>
        <button type='button' className='btn btn-danger' disabled={!isLoggedIn}>
          Delete
        </button>
      </div>
    </li>
  )) : 'There is no books available!'; 

  return (
    <div>
      <h2>Books List</h2>

      {isLoading ? 'loading...' : (
        <ul className='list-group'>
          {bookList}
        </ul>
      )
      }
    </div>
  );
};

export default BooksList;
